INSERT INTO `wp_posts` VALUES("77", "1", "2018-07-12 14:07:39", "2018-07-12 14:07:39", "<div class=\"col-md-5\" id=\"section_one\">\r\n<h3><strong>About MES Forum</strong></h3>\r\nMechanical Engineering Students Forum (MES FORUM), Department of Mechanical Engineering, Guru Nanak Dev Engineering college, Ludhiana was established/constuted with the assistance and involvement of senior faculty of department to take care of academic/ professional needs of the under graduate (UG) Students of the department.\r\n\r\n</div>\r\n<div class=\"col-md-6 col-md-offset-1\"><img src=\"http://localhost/mes/wp-content/uploads/2018/07/unnamed.jpg\" alt=\"\" width=\"279\" height=\"299\" class=\"alignnone size-full wp-image-9\" /></div>\r\n\r\n<hr />\r\n\r\n<div style=\"color: white; padding: 10px;\">\r\n\r\n<h3>OBJECTIVES</h3>\r\n To plan and organize technical programs and activities, such as special lectures, workshops, seminars, symposia, conferences, exbitions etc. for the benefit of the students on a regular basis.\r\n\r\n To provide a common platform to a students to exchange ideas and information on the topics of their interest, curriculum, employment/ higher education opportunities, emerging trends, new development etc.\r\n\r\n To facilitate technical visits / practical training / project work / employment of the students in R&amp;D Laboratories, industries, academic institutions etc.\r\n\r\n To encourage team work and the spirit of self reliance among the students.\r\n\r\n To serve focal point at department level on all aspects of professional development of the students.\r\n\r\n To assist the department in arrangement and management of academic activities.\r\n\r\n&nbsp;\r\n\r\n</div>\r\n<div id=\"section_two\"></div>", "Front Page", "", "inherit", "closed", "closed", "", "22-revision-v1", "", "", "2018-07-12 14:07:39", "2018-07-12 14:07:39", "", "22", "http://localhost/mes/2018/07/12/22-revision-v1/", "0", "revision", "", "0");
