INSERT INTO `wp_posts` VALUES("15", "1", "2018-07-12 11:26:07", "2018-07-12 11:26:07", "{\n    \"hestia::hestia_features_subtitle\": {\n        \"value\": \"MESF, the Mechanical Engineering Students Forum of GNDEC Ludhiana, aims to play a pivotal role in the development of students as engineers by various out-of-curriculum and extra curricular activities. MESF aims to inculcate among its members an awareness and appreciation of the various disciplines of not just Mechanical Engineering but also other relevant fields. By way of its activities MESF aims to be a platform for all the students of GNDEC Ludhiana in general and particularly of the students of ME department. MESF seeks to be an active organization of the ME department at GNDEC which promotes their career interests.\",\n        \"type\": \"theme_mod\",\n        \"user_id\": 1,\n        \"date_modified_gmt\": \"2018-07-12 11:26:07\"\n    }\n}", "", "", "trash", "closed", "closed", "", "eea0018c-69f8-4a64-8ddf-6ec332b3674c", "", "", "2018-07-12 11:26:07", "2018-07-12 11:26:07", "", "0", "http://localhost/mes/?p=15", "0", "customize_changeset", "", "0");
