INSERT INTO `wp_posts` VALUES("25", "1", "2018-07-12 14:08:45", "2018-07-12 14:08:45", "<div class=\"col-md-5\" id=\"section_one\">\n<h3><strong>About MES Forum</strong></h3>\nMechanical Engineering Students Forum (MES FORUM), Department of Mechanical Engineering, Guru Nanak Dev Engineering college, Ludhiana was established/constuted with the assistance and involvement of senior faculty of department to take care of academic/ professional needs of the under graduate (UG) Students of the department.\n\n</div>\n<div class=\"col-md-6 col-md-offset-1\"><img src=\"http://localhost/mes/wp-content/uploads/2018/07/unnamed.jpg\" alt=\"\" width=\"279\" height=\"299\" class=\"alignnone size-full wp-image-9\" /></div>\n\n<hr />\n\n<div style=\"; padding: 10px;\">\n\n<h3>OBJECTIVES</h3>\n To plan and organize technical programs and activities, such as special lectures, workshops, seminars, symposia, conferences, exbitions etc. for the benefit of the students on a regular basis.\n\n To provide a common platform to a students to exchange ideas and information on the topics of their interest, curriculum, employment/ higher education opportunities, emerging trends, new development etc.\n\n To facilitate technical visits / practical training / project work / employment of the students in R&amp;D Laboratories, industries, academic institutions etc.\n\n To encourage team work and the spirit of self reliance among the students.\n\n To serve focal point at department level on all aspects of professional development of the students.\n\n To assist the department in arrangement and management of academic activities.\n\n&nbsp;\n\n</div>\n<div id=\"section_two\"></div>", "Front Page", "", "inherit", "closed", "closed", "", "22-autosave-v1", "", "", "2018-07-12 14:08:45", "2018-07-12 14:08:45", "", "22", "http://localhost/mes/2018/07/12/22-autosave-v1/", "0", "revision", "", "0");
INSERT INTO `wp_posts` VALUES("26", "1", "2018-07-12 11:52:50", "2018-07-12 11:52:50", "<div class=\"col-md-5\"><p><strong>About MES Forum</strong></p><p>Mechanical Engineering Students Forum (MES FORUM), Department of Mechanical Engineering, Guru Nanak Dev Engineering college, Ludhiana was established/constuted with the assistance and involvement of senior faculty of department to take care of academic/ professional needs of the under graduate (UG) Students of the department.</p></div><div class=\"col-md-6 col-md-offset-1\"><img src=\"http://localhost/mes/wp-content/uploads/2018/07/unnamed.jpg\" alt=\"\" width=\"279\" height=\"299\" class=\"alignnone size-full wp-image-9\" /></div>\r\n\r\n<body>\r\n\r\n<h1>OBJECTIVES</h1>\r\n<p>\r\nThe MES FORUM have the following broad objectives :\r\n	To plan and organize technical programs and activities, such as special lectures, workshops, seminars, symposia, conferences, exbitions etc. for the benefit of the students on a regular basis.\r\n	To provide a common platform to a students to exchange ideas and information on the topics of their interest, curriculum, employment/ higher education opportunities, emerging trends, new development etc.\r\n	To facilitate technical visits / practical training / project work / employment of the students in R&D Laboratories, industries, academic institutions etc.\r\n	To encourage team work and the spirit of self reliance among the students.\r\nTo serve focal point at department level on all aspects of professional development of the students.\r\n	To assist the department in arrangement and management of academic activities.\r\n</p>\r\n\r\n</body>", "Front Page", "", "inherit", "closed", "closed", "", "22-revision-v1", "", "", "2018-07-12 11:52:50", "2018-07-12 11:52:50", "", "22", "http://localhost/mes/2018/07/12/22-revision-v1/", "0", "revision", "", "0");
