INSERT INTO `wp_posts` VALUES("56", "1", "2018-07-12 13:05:18", "2018-07-12 13:05:18", "{\n    \"hestia::hestia_contact_content_new\": {\n        \"value\": \"<p><b>Membership</b><span>\\u00a0is proposed that initially all the under graduate (UG) students of the departments be considered as the members of MES FORUM. Later on some additional guidelines about membership may be framed it the need arises.</span><br /><br /><b>MEMBERSHIP FEE/ REGISTRATION FEE/ FUNDS</b><br /><span>Initially no membership fee will be charged from the members of MES FORUM. Later on nominal membership fee may be taken it circumstances desire so and separate bank account can be opened to manage the funds. In the beginning the activities and meetings will be arranged using nominal monetary contribution of the participants/members in the form of registration fee (it is requisite for particular activity) or department may sponsor the same or sponsorships may be arranged from Industrial commercial/govt. and non govt. organizations. The coordinator will make</span><br /><span>proposal in consultation with the organizing secretaries and finance secretaries for budget of a particular activity and get it approved from the Head of department/ Director through Chairman and General Secretaries. The finance secretaries will keep and maintain the record of inance related to the MES and get it audited once in a year by committee framed by Head of department Director. The purchase and expenditure will be done as per the institutional rules and regulations.</span></p>\",\n        \"type\": \"theme_mod\",\n        \"user_id\": 1,\n        \"date_modified_gmt\": \"2018-07-12 13:05:18\"\n    },\n    \"hestia::hestia_contact_subtitle\": {\n        \"value\": \"\",\n        \"type\": \"theme_mod\",\n        \"user_id\": 1,\n        \"date_modified_gmt\": \"2018-07-12 13:05:18\"\n    },\n    \"hestia::hestia_contact_title\": {\n        \"value\": \"Membership\",\n        \"type\": \"theme_mod\",\n        \"user_id\": 1,\n        \"date_modified_gmt\": \"2018-07-12 12:59:09\"\n    },\n    \"hestia::hestia_contact_background\": {\n        \"value\": \"\",\n        \"type\": \"theme_mod\",\n        \"user_id\": 1,\n        \"date_modified_gmt\": \"2018-07-12 13:05:18\"\n    }\n}", "", "", "trash", "closed", "closed", "", "29de2439-5068-4b2a-a2b9-d0e91304c33b", "", "", "2018-07-12 13:05:18", "2018-07-12 13:05:18", "", "0", "http://localhost/mes/?p=56", "0", "customize_changeset", "", "0");
